// Create dynamic variables array
const dynamicVars = [
  {
    "tag": "first_name",
    "value": $input.first().json.first_name || ""
  }
];

return dynamicVars.map(item => ({ json: item }));

// Get the dynamic assistant data
let dynamicAssistant = $node['Set Dynamic Assistant'].json;

// Process each dynamic variable
for (const item of $input.all()) {
  const tag = item.json.tag;
  const value = item.json.value;

  // Convert assistant object to string for replacement
  let assistantString = JSON.stringify(dynamicAssistant);

  // Replace the placeholder with actual value
  assistantString = assistantString.replace(`[${tag}]`, value);

  // Convert back to object
  dynamicAssistant = JSON.parse(assistantString);
}

// Return the updated assistant configuration
return [{ json: dynamicAssistant }];

// Get the processed assistant data
const assistantData = $input.first().json;

// Whitelist only the required keys
const whitelistedKeys = [
  'name',
  'model', 
  'voice',
  'serverUrl',
  'transcriber',
  'analysisPlan',
  'firstMessage',
  'hipaaEnabled',
  'clientMessages',
  'endCallMessage',
  'endCallPhrases',
  'serverMessages',
  'backgroundSound',
  'recordingEnabled',
  'voicemailMessage',
  'endCallFunctionEnabled',
  'dialKeypadFunctionEnabled'
];

// Create filtered object with only whitelisted keys
const filteredAssistant = {};
for (const key of whitelistedKeys) {
  if (assistantData.hasOwnProperty(key)) {
    filteredAssistant[key] = assistantData[key];
  }
}

return [{ json: filteredAssistant }];

// Take the input object and convert to JSON string
const inputObject = $input.first().json;

// Transform to JSON with no spacing (equivalent to space: "")
const jsonString = JSON.stringify(inputObject);

return [{ 
  json: {
    jsonString: jsonString,
    object: inputObject
  }
}];

