// UTM Parameters Extract karna
const bodyData = $input.item.json.body || $input.item.json;

// Lead data extract karna
const leadData = {
  name: bodyData.name || '',
  email: bodyData.email || '',
  phone: bodyData.phone || '',
  message: bodyData.message || '',

  // UTM Parameters
  utm_source: bodyData.utm_source || 'direct',
  utm_medium: bodyData.utm_medium || 'none',
  utm_campaign: bodyData.utm_campaign || 'none',
  utm_term: bodyData.utm_term || 'none',
  utm_content: bodyData.utm_content || 'none',

  // Lead Source Identify karna
  lead_source: '',
  lead_type: '',

  // Timestamp
  timestamp: new Date().toISOString(),
  date: new Date().toLocaleDateString('en-GB'),
  time: new Date().toLocaleTimeString('en-GB')
};

// Lead Source Logic
if (leadData.utm_source === 'direct' && leadData.utm_medium === 'none') {
  leadData.lead_source = 'Organic/Direct';
  leadData.lead_type = 'Organic';
} else if (leadData.utm_medium === 'cpc' || leadData.utm_medium === 'ppc') {
  leadData.lead_source = 'Paid Ads';
  leadData.lead_type = 'Paid';
} else if (leadData.utm_source === 'google' || leadData.utm_source === 'facebook' || leadData.utm_source === 'instagram') {
  leadData.lead_source = `${leadData.utm_source.charAt(0).toUpperCase() + leadData.utm_source.slice(1)} Ads`;
  leadData.lead_type = 'Paid';
} else if (leadData.utm_medium === 'organic') {
  leadData.lead_source = 'Organic Search';
  leadData.lead_type = 'Organic';
} else if (leadData.utm_medium === 'social') {
  leadData.lead_source = 'Social Media';
  leadData.lead_type = 'Organic';
} else if (leadData.utm_medium === 'email') {
  leadData.lead_source = 'Email Campaign';
  leadData.lead_type = 'Marketing';
} else {
  leadData.lead_source = `${leadData.utm_source} - ${leadData.utm_medium}`;
  leadData.lead_type = 'Other';
}

return { json: leadData };
