// Sort by oldest first, then map to your desired shape

const roleNames = [
  "Web Developer", 
  "SEO Specialist", 
  "GHL Specialist", 
  "Google Ads", 
  "Project Manager / Admin", 
  "Graphics/Video Editor", 
  "Technical Lead / Tech Support"
];

function formatDate(timestamp) {
  if (!timestamp) return "";
  const num = parseInt(timestamp, 10);
  if (isNaN(num)) return "";
  const date = new Date(num);
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0'); // 0-based
  const year = date.getFullYear();
  return `${month}/${day}/${year}`;
}

// 1) Pull in all items from previous node
const items = $input.all();

// 2) Sort by date_created ascending (oldest → newest)
items.sort((a, b) => {
  const dateA = Number(a.json?.date_created ?? 0);
  const dateB = Number(b.json?.date_created ?? 0);
  return dateA - dateB;
});

// 3) Map to your desired output structure
return items.map(task => {
  const t = task.json || {};
  const customFields = t.custom_fields || [];

  // Role field lookup
  const roleField = customFields.find(f => f.id === "a1fc0839-bad7-4f29-b1dc-d08ba5026352");
  let roleValue = "";
  if (roleField && roleField.value !== null && roleField.value !== undefined) {
    const value = roleField.value;
    const orderIndex = typeof value === "object" ? value?.orderindex : Number(value);
    if (!isNaN(orderIndex)) {
      roleValue = roleNames[orderIndex] || "";
    }
  }

  // Website field lookup
  const websiteField = customFields.find(f => f.id === "a967eeae-631a-4db2-868a-be861aa92052");
  const websiteUrl = websiteField?.value || "";

  // Priority text (handle object or null)
  const priorityText = t.priority?.priority || "";

  return {
    json: {
      "Task ID": t.id,
      "Task Name": t.name,
      "Status": t.status?.status || "",
      "Due Date": formatDate(t.due_date),
      "Assignee": Array.isArray(t.assignees) ? t.assignees.map(a => a.username).join(", ") : "",
      "Description": t.description || "",
      "Date Created": formatDate(t.date_created),
      "Date Updated": formatDate(t.date_updated),
      "Date Closed": formatDate(t.date_closed),
      "Date Done": formatDate(t.date_done),
      "URL": t.url,
      "Priority": priorityText,
      "Role": roleValue,
      "Website": websiteUrl
    }
  };
});

// Extract role information from ClickUp task update event
const roleUpdate = $input.all().find(item => 
  item.json.history_items && 
  item.json.history_items.some(hi => hi.field === 'custom_field' && hi.custom_field.name === 'Role')
);

if (!roleUpdate) {
  return [];
}

// Get the role change details
const roleChange = roleUpdate.json.history_items.find(hi => 
  hi.field === 'custom_field' && hi.custom_field.name === 'Role'
);

if (!roleChange) {
  return [];
}

// Extract role options from the custom field definition
const roleOptions = roleChange.custom_field.type_config.options;

// Find the selected role
const selectedRole = roleOptions.find(opt => opt.id === roleChange.after);

// Prepare output
return [{
  json: {
    task_id: roleUpdate.json.task_id,
    role_id: roleChange.after,
    role_name: selectedRole ? selectedRole.name : 'Unknown Role',
    role_orderindex: selectedRole ? selectedRole.orderindex : -1,
    updated_by: roleChange.user.username,
    update_timestamp: new Date(parseInt(roleChange.date)).toISOString()
  }
}];

// Ensure your helper functions are defined first:
function padTo2(num) {
  return num.toString().padStart(2, '0');
}

function formatDateDDMMYYYY(date) {
  const d = padTo2(date.getDate());
  const m = padTo2(date.getMonth() + 1);
  const y = date.getFullYear();
  return `${m}/${d}/${y}`;
}

// Now do the mapping for both created and updated dates:
return $input.all().map(item => {
  // Convert the timestamps to proper Date objects
  const createdTs = parseInt(item.json.date_created, 10);
  const updatedTs = parseInt(item.json.date_updated, 10);

  return {
    json: {
      task_id:       item.json.id,
      task_name:     item.json.name,
      date_created:  formatDateDDMMYYYY(new Date(createdTs)),
      date_updated:  formatDateDDMMYYYY(new Date(updatedTs))
    }
  };
});

