// Normalize and trim events for AI processing
const MAX_EVENTS = 120;
const MAX_CONTENT_LENGTH = 8000;

// Get data from previous nodes
const items = $input.all();

// Helper to safely extract data
function safeExtract(items, key) {
  for (let item of items) {
    if (item.json[key]) return item.json[key];
    if (item.json.body && item.json.body[key]) return item.json.body[key];
    if (item.json.data && item.json.data[key]) return item.json.data[key];
  }
  return [];
}

const contact = items[0]?.json?.contact || items[0]?.json || {};
const notes = safeExtract(items, 'notes');
const conversations = safeExtract(items, 'conversations');
const appointments = safeExtract(items, 'appointments');
const opportunities = safeExtract(items, 'opportunities');

// Normalize to standard event format
function toEvent(type, obj) {
  const content = (obj.body || obj.message || obj.transcript || obj.note || obj.description || JSON.stringify(obj)).toString();

  return {
    type,
    id: obj.id || obj._id || obj.eventId || `${type}_${Math.random().toString(36).substr(2, 6)}`,
    timestamp: obj.date || obj.createdAt || obj.dateAdded || obj.updatedAt || new Date().toISOString(),
    direction: obj.direction || obj.type || 'internal',
    participant: obj.userName || obj.userId || obj.agentName || obj.user || null,
    content: content.slice(0, MAX_CONTENT_LENGTH),
    attachments: obj.attachments || [],
    metadata: {
      status: obj.status,
      appointmentStatus: obj.appointmentStatus,
      pipelineStage: obj.pipelineStage,
      monetaryValue: obj.monetaryValue,
      raw: obj
    }
  };
}

let events = [];

// Process each data type
if (Array.isArray(notes)) {
  notes.forEach(n => events.push(toEvent('note', n)));
}

if (Array.isArray(conversations)) {
  conversations.forEach(c => {
    if (c.messages && Array.isArray(c.messages)) {
      c.messages.forEach(m => events.push(toEvent('sms', m)));
    } else {
      events.push(toEvent('conversation', c));
    }
  });
}

if (Array.isArray(appointments)) {
  appointments.forEach(a => events.push(toEvent('appointment', a)));
}

if (Array.isArray(opportunities)) {
  opportunities.forEach(o => events.push(toEvent('deal', o)));
}

// Sort by timestamp (most recent first)
events.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

const totalEvents = events.length;
let truncated = false;

if (events.length > MAX_EVENTS) {
  events = events.slice(0, MAX_EVENTS);
  truncated = true;
}

return {
  json: {
    contactId: contact.id,
    contactName: `${contact.firstName || ''} ${contact.lastName || ''}`.trim(),
    contactEmail: contact.email,
    contactPhone: contact.phone,
    events,
    metadata: {
      totalEvents,
      includedEvents: events.length,
      truncated,
      generatedAt: new Date().toISOString()
    }
  }
};

// Build AI prompt with strict JSON schema
const data = $input.first().json;

const systemMessage = `You are a deterministic data summarizer for a real estate CRM. Produce EXACTLY one JSON object that matches the schema provided below. Do NOT provide any explanatory text before or after the JSON. Use 'unknown' when evidence is missing. Use ISO dates YYYY-MM-DD for dates. Output must be valid JSON only.`;

const schema = {
  contact_id: "string",
  summary_generated_at: "ISO8601",
  deal_closed: "yes|no|unknown",
  canceled_deals: [{
    deal_id: "string",
    date: "YYYY-MM-DD",
    reason: "string"
  }],
  has_sent_properties: "yes|no|unknown",
  properties_shared: [{
    property_id: "string",
    date: "YYYY-MM-DD",
    status: "passed|offer_submitted|other",
    notes: "string"
  }],
  offers_submitted: [{
    property_id: "string",
    amount: "number",
    date: "YYYY-MM-DD",
    status: "accepted|rejected|pending",
    notes: "string"
  }],
  relationship_status: "string",
  history_bullets: ["string"],
  recommended_next_steps: ["call|email|send-doc|no-action"],
  confidence_score: "0.0-1.0"
};

const rules = `
RULES:
1) Set deal_closed=yes if there is any event showing a deal moved to won/closed. Set 'no' if explicitly lost/refunded. Else unknown.
2) canceled_deals: include deals where text shows 'cancel' or status updated to canceled. Extract date and reason if present.
3) has_sent_properties: yes if messages/notes include property links/attachments or explicit 'sent you listings'.
4) properties_shared: find property identifiers (URLs or IDs). Mark status as 'offer_submitted' if any event mentions 'offer submitted' for that property.
5) offers_submitted: extract offers found in notes/calls/emails; include numeric amount if present.
6) relationship_status: one short sentence summarizing engagement and trust level.
7) history_bullets: 3-5 key events in chronological order.
8) recommended_next_steps: array of actions based on current status.
9) confidence_score: 0.0-1.0 representing evidence completeness and data quality.
10) If events were truncated, note this in history_bullets.
`;

const userMessage = `SCHEMA:
${JSON.stringify(schema, null, 2)}

${rules}

CONTACT INFO:
ID: ${data.contactId}
Name: ${data.contactName}
Email: ${data.contactEmail}
Phone: ${data.contactPhone}

INPUT_EVENTS (${data.events.length} events, truncated: ${data.metadata.truncated}):
${JSON.stringify(data.events, null, 2)}

Now produce the single JSON object matching the schema exactly. Output ONLY the JSON, nothing else.`;

return {
  json: {
    messages: [
      { role: "system", content: systemMessage },
      { role: "user", content: userMessage }
    ],
    metadata: data.metadata
  }
};

// Validate and parse AI JSON response
const response = $input.first().json;
const aiText = response.choices[0].message.content.trim();

let parsedSummary;
let parseError = null;

try {
  // Try to extract JSON if there's text around it
  const jsonMatch = aiText.match(/\{[\s\S]*\}/);
  const jsonStr = jsonMatch ? jsonMatch[0] : aiText;

  parsedSummary = JSON.parse(jsonStr);

  // Validate required fields
  const requiredFields = ['contact_id', 'summary_generated_at', 'deal_closed', 'relationship_status'];
  for (let field of requiredFields) {
    if (!(field in parsedSummary)) {
      throw new Error(`Missing required field: ${field}`);
    }
  }

} catch (error) {
  parseError = error.message;
  console.error('AI JSON Parse Error:', error);
  console.error('Raw AI Response:', aiText);

  // Create fallback summary
  parsedSummary = {
    contact_id: $('Extract Contact ID').first().json.contactId,
    summary_generated_at: new Date().toISOString(),
    deal_closed: "unknown",
    canceled_deals: [],
    has_sent_properties: "unknown",
    properties_shared: [],
    offers_submitted: [],
    relationship_status: "Unable to generate summary - parsing error",
    history_bullets: ["Error processing contact history"],
    recommended_next_steps: ["no-action"],
    confidence_score: 0.0,
    error: parseError,
    raw_ai_response: aiText.substring(0, 500)
  };
}

// Create human-readable summary
const humanSummary = `${parsedSummary.relationship_status}\n\nKey Points:\n${parsedSummary.history_bullets.slice(0, 3).map(b => 'â€¢ ' + b).join('\n')}\n\nNext Steps: ${parsedSummary.recommended_next_steps.join(', ')}`;

return {
  json: {
    summary: parsedSummary,
    humanSummary,
    parseError,
    tokensUsed: response.usage?.total_tokens || 0
  }
};

