// Handle workflow errors
const error = $input.first().json.error || $input.first().json;

const errorResponse = {
  success: false,
  error: {
    message: error.message || 'Workflow error occurred',
    code: error.code || 'WORKFLOW_ERROR',
    step: error.node || 'unknown',
    timestamp: new Date().toISOString()
  }
};

console.error('Workflow Error:', JSON.stringify(errorResponse, null, 2));

return { json: errorResponse };
