{
  "type": "object",
  "title": "Email Output Schema",
  "description": "JSON schema for travel email output containing subject line and responsive HTML content",
  "required": ["subject", "content"],
  "properties": {
    "subject": {
      "type": "string",
      "description": "Engaging email subject line incorporating destination and travel dates, optimally under 50 characters",
      "maxLength": 100
    },
    "content": {
      "type": "string",
      "description": "Complete responsive HTML email content with inline CSS styling, presenting all travel plan details in a professional, user-friendly format optimised for email clients"
    },
    "summary": {
      "type": "string",
      "description": "Summary of the email content"
    }
  }
}

{
  "type": "object",
  "title": "Travel Plan Output Schema",
  "description": "JSON schema for travel planning output containing flights, activities, accommodations, and request summary with image support",
  "required": ["flights", "activities", "accommodations", "request_info"],
  "properties": {
    "flights": {
      "type": "array",
      "maxItems": 3,
      "description": "Array of flight recommendations (maximum 3)",
      "items": {
        "type": "object",
        "required": ["flight_number", "airline", "departure_airport", "arrival_airport", "departure_time", "arrival_time", "duration", "stops", "price", "booking_class", "aircraft_type", "image_url"],
        "properties": {
          "flight_number": {
            "type": "string",
            "description": "Flight number identifier"
          },
          "airline": {
            "type": "string",
            "description": "Airline name"
          },
          "departure_airport": {
            "type": "string",
            "description": "Departure airport code or name"
          },
          "arrival_airport": {
            "type": "string",
            "description": "Arrival airport code or name"
          },
          "departure_time": {
            "type": "string",
            "description": "Departure time with date and timezone"
          },
          "arrival_time": {
            "type": "string",
            "description": "Arrival time with date and timezone"
          },
          "duration": {
            "type": "string",
            "description": "Total flight duration"
          },
          "stops": {
            "type": "string",
            "description": "Number and location of stops or 'Direct'"
          },
          "price": {
            "type": "string",
            "description": "Flight price with currency"
          },
          "booking_class": {
            "type": "string",
            "description": "Booking class (Economy, Business, First, etc.)"
          },
          "aircraft_type": {
            "type": "string",
            "description": "Aircraft model and type"
          },
          "image_url": {
            "type": "string",
            "description": "URL to airline logo or aircraft image, 'Not available' if not provided"
          }
        }
      }
    },
    "activities": {
      "type": "array",
      "maxItems": 5,
      "description": "Array of activity recommendations (maximum 5)",
      "items": {
        "type": "object",
        "required": ["name", "description", "location", "address", "duration", "price", "category", "rating", "booking_required", "opening_hours", "best_time_to_visit", "image_url"],
        "properties": {
          "name": {
            "type": "string",
            "description": "Activity name or title"
          },
          "description": {
            "type": "string",
            "description": "Detailed activity description"
          },
          "location": {
            "type": "string",
            "description": "General location or area"
          },
          "address": {
            "type": "string",
            "description": "Specific address"
          },
          "duration": {
            "type": "string",
            "description": "Estimated duration for the activity"
          },
          "price": {
            "type": "string",
            "description": "Activity cost with currency or 'Free'"
          },
          "category": {
            "type": "string",
            "description": "Activity category (cultural, adventure, dining, etc.)"
          },
          "rating": {
            "type": "string",
            "description": "Rating score out of 5 or review platform rating"
          },
          "booking_required": {
            "type": "boolean",
            "description": "Whether advance booking is required"
          },
          "opening_hours": {
            "type": "string",
            "description": "Operating hours or schedule"
          },
          "best_time_to_visit": {
            "type": "string",
            "description": "Recommended time of day or season to visit"
          },
          "image_url": {
            "type": "string",
            "description": "URL to activity or attraction image, 'Not available' if not provided"
          }
        }
      }
    },
    "accommodations": {
      "type": "array",
      "description": "Array of accommodation recommendations",
      "items": {
        "type": "object",
        "required": ["name", "address", "location_description", "accommodation_type", "room_type", "price_per_night", "total_price", "check_in_date", "check_out_date", "amenities", "rating", "review_count", "cancellation_policy", "distance_to_city_centre", "image_url"],
        "properties": {
          "name": {
            "type": "string",
            "description": "Property name"
          },
          "address": {
            "type": "string",
            "description": "Full property address"
          },
          "location_description": {
            "type": "string",
            "description": "Neighbourhood or area description"
          },
          "accommodation_type": {
            "type": "string",
            "description": "Type of accommodation (hotel, hostel, apartment, etc.)"
          },
          "room_type": {
            "type": "string",
            "description": "Room category or type"
          },
          "price_per_night": {
            "type": "string",
            "description": "Nightly rate with currency"
          },
          "total_price": {
            "type": "string",
            "description": "Total cost for entire stay with currency"
          },
          "check_in_date": {
            "type": "string",
            "description": "Check-in date"
          },
          "check_out_date": {
            "type": "string",
