// Initialize combined text
let combinedText = '';

// Check if ParsedResults exists and is an array
if ($input.all()[0].json.ParsedResults && Array.isArray($input.all()[0].json.ParsedResults)) {
  // Loop through each parsed result
  $input.all()[0].json.ParsedResults.forEach(result => {
    // Add parsed text if it exists
    if (result.ParsedText) {
      combinedText += result.ParsedText + '\n\n'; // Add double newline between pages
    }
  });

  // Trim any extra whitespace
  combinedText = combinedText.trim();
}

// Return the combined text
return {
  text: combinedText,
  total_pages: $input.all()[0].json.ParsedResults ? $input.all()[0].json.ParsedResults.length : 0
};

