const raw = $json;
const isCallback = !!raw.callback_query;
const msg = isCallback ? raw.callback_query.message : raw.message || raw;

// Extract chat_id and username
const caption = msg.caption || null;
const chatId = msg.chat?.id || null;
const username = isCallback
  ? raw.callback_query.from?.username || null
  : raw.message?.from?.username || raw.from?.username || null;

// Extract user_text, route_id, and message_id from callback_data
let userText = null;
let routeId = null;
let callbackMessageId = null;

if (isCallback && raw.callback_query.data) {
  const parts = raw.callback_query.data.split(";");
  userText = parts[0] || null;           // e.g. /delete or /correct
  routeId = parts[1] || null;            // e.g. rec4oL52wQpba9u7F
  callbackMessageId = parts[2] || null;  // e.g. 225
} else {
  userText = msg.text || msg.caption || null;
}

// Return early for callback
if (isCallback) {
  return {
    chat_id: chatId,
    username,
    file_id: null,
    user_text: userText,
    route_id: routeId,
    message_id: callbackMessageId,
    file_type: 'callback',
    is_callback: true
  };
}

// Handle photo
if (msg.photo && msg.photo.length > 0) {
  const largestPhoto = msg.photo[msg.photo.length - 1];
  return {
    chat_id: chatId,
    caption: caption,
    username,
    file_id: largestPhoto.file_id,
    user_text: userText,
    file_type: 'photo',
    is_callback: false,
    photo_metadata: {
      sizes: msg.photo.length,
      width: largestPhoto.width,
      height: largestPhoto.height
    }
  };
}

// Handle documents
if (msg.document) {
  const mimeType = msg.document.mime_type || '';
  const isImage = mimeType.startsWith('image/');

  return {
    chat_id: chatId,
    username,
    file_id: msg.document.file_id,
    user_text: userText,
    file_type: isImage ? 'photo' : 'document',
    mime_type: mimeType,
    is_callback: false
  };
}

// Handle voice messages
if (msg.voice) {
  return {
    chat_id: chatId,
    username,
    file_id: msg.voice.file_id,
    user_text: userText,
    file_type: 'voice',
    duration: msg.voice.duration || null,
    mime_type: msg.voice.mime_type || null,
    is_callback: false
  };
}

// Handle plain text
const firstInput = $input.first();
const entities = firstInput?.json?.message?.entities || [];
const firstEntity = entities.length > 0 ? entities[0] : null;

return {
  chat_id: chatId,
  username,
  file_id: null,
  user_text: userText,
  file_type: 'text',
  type: firstEntity?.type || null,
  is_callback: false
};
