// Input example: [{"documentId": "1vQwy2sH4eoRNKbK8pUGjvuutbinU18xpd6v1OpkFn-Y"}]

const docId = items[0].json.documentId;

if (!docId) {
  throw new Error("‚ùå No documentId found in input!");
}

// Construct the Google Docs URL
const docUrl = `https://docs.google.com/document/d/${docId}/edit`;

return [
  {
    json: {
      documentId: docId,
      documentLink: docUrl
    }
  }
];

// Get the raw string output from the previous node
const raw = $input.first().json.output;

// Step 1: Remove code fences and extra whitespace
const cleaned = raw.replace(/```json/g, '').replace(/```/g, '').trim();

// Step 2: Parse the cleaned JSON safely
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error('‚ùå Failed to parse JSON: ' + err.message + '\n\nContent:\n' + cleaned);
}

// Step 3: Return a clean, mappable object
return [
  {
    json: {
      title: parsed.title || "",
      content: parsed.output || ""
    }
  }
];

