// Get the URN from previous node
const urn = $input.first().json.urn;

// Extract the share ID (the part after "urn:li:share:")
const shareId = urn.split(":").pop();

// Build the LinkedIn public post URL
const postUrl = `https://www.linkedin.com/feed/update/urn:li:share:${shareId}/`;

// Return the final result
return [
  {
    json: {
      post_url: postUrl
    }
  }
];
